CREATE OR REPLACE FORCE VIEW "POS_REPLENISHMENT" ("POS_CODE", "POS_BALANCE", "POS_DESCRIPTION", "SERIAL_NUMBER", "MERCHANT_NAME", "CSP_ID", "POS_STATUS", "CURRENCY") AS (SELECT TERMINALS.CODE,
    TERMINALS.BALANCE,
    TERMINALS.DESCRIPTION_PRIMARY_LANG,
    TERMINAL_DETAILS.DEVICE_ID,
    MERCHANTS.NAME_PRIMARY_LANG,
    MERCHANTS.CSP_ID,
    TERMINAL_STATUS.NAME_PRIMARY_LANG,
    CURRENCY.CODE
  FROM POS_SOF.POS_TERMINALS TERMINALS
  INNER JOIN POS_SOF.MERCHANTS_LOOKUP MERCHANTS
  ON TERMINALS.MERCHANT_ID = MERCHANTS.ID
  INNER JOIN POS_SOF.POS_TERMINAL_DETAILTS TERMINAL_DETAILS
  ON TERMINALS.ID = TERMINAL_DETAILS.POS_TERMINAL_ID
  INNER JOIN POS_SOF.TERMINAL_STATUS_LOOKUP TERMINAL_STATUS
  ON TERMINALS.TERMINAL_STATUS_ID = TERMINAL_STATUS.ID
  INNER JOIN POS_SOF.CURRENCY CURRENCY
  ON CURRENCY.ID                  =TERMINALS.CURRENCY_ID
  WHERE TERMINAL_STATUS.CODE NOT IN ('DLTD')
  )/* Build # SEE_REP_059 */;

-- changeset Mostafa.Abdullatif:1658241639693-16
CREATE OR REPLACE FORCE VIEW "SOF_ACTION_DETAILS_VIEW" ("ACCOUNT_NUMBER", "ACCOUNT_TYPE", "ACCOUNT_USAGE", "TRX_AMOUNT", "BILL_TYPE_CODE", "FW_CUST_REF_NUM", "PMT_DESCRIPTION", "PMT_TYPE", "GW_DATE", "SOF_DATE", "ACTION_TYPE", "BILL_TYPES_TYPE", "CSP_CODE", "ACTION_NATURE", "USER_NAME", "ACTION_ID") AS SELECT acc.code AS account_number,
          (case when acctType.code in ('Default', 'SDA') then 'Debit' else 'Credit' end ) AS account_type,
          USG.code AS account_usage, COALESCE(tr.trx_amount,acts.AMOUNT) AS trx_amount,
          tr.bill_type_code,tr.fw_cust_ref_num, tr.pmt_description, tr.pmt_type,
          tr.trx_client_date AS gw_date, tr.trx_date AS sof_date,
          actl.code AS action_type, bttl.code AS bill_types_type,
          C.CODE AS CSP_CODE, actl.ACTION_NATURE,USR.user_name,acts.ACTION_ID
  FROM   pos_sof.account_actions acts
          LEFT OUTER JOIN pos_sof.action_types_lookup actl ON acts.action_type_id = actl.ID
          LEFT OUTER JOIN pos_sof.accounts acc ON acts.terminal_id = acc.ID
          LEFT OUTER JOIN pos_sof.account_types acctType ON acc.account_type_id = acctType.ID
          LEFT OUTER JOIN POS_SOF.USAGE_LOOKUP USG on acc.USAGE_ID = USG.ID
          LEFT OUTER JOIN pos_sof.transaction_items tr ON acts.id = tr.transaction_id
          left outer join POS_SOF.TRANSACTION_ITEMS tr1 on acts.id = tr.REVERSAL_TRANSACTION_ID and acts.ACTION_TYPE_ID = 4
          LEFT OUTER JOIN POS_SOF.CUSTOMER_PROFILE CP ON acc.CUSTOMER_PROFILE_ID = CP.ID
          LEFT OUTER JOIN POS_SOF.CSP C ON CP.CSP_ID = C.ID
          LEFT OUTER JOIN ebpp_core.bill_types bt ON tr.bill_type_code = bt.code
          LEFT OUTER JOIN ebpp_core.bill_types_type_lookup bttl ON bt.type_id = bttl.ID
          left outer join POS_SOF.USERS USR on acts.ACTOR_ID= USR.ID;

