-- changeset Mostafa.Abdullatif:1658241639693-18
CREATE OR REPLACE FORCE VIEW "POS_TRANSACT" ("CSP_NAME", "POS_TERMINAL_NUMBER", "POS_TERMINAL_CODE", "FEES", "OLD_PALANCE", "NEW_PALANCE", "AMOUNT", "ACTION_DATE", "ACTION_ID", "ACTION_NATURE", "USER_NAME", "MERCHANT_CODE", "MERCHANT_NAME", "ACTION_CODE", "ACTION_TYPE", "GW_TRX_DATE", "FW_CUST_REF_NUM", "USAGE_TYPE", "DESCRIPTION", "ACCOUNT_CURRENCY", "AMOUNT_IN_ACCT_CURRENCY", "TRX_CURRENCY", "EXCHANGE_RATE", "CASH_COLLEC", "BILL_TYPE_CODE", "BILLING_ACCOUNT_NUMBER") AS SELECT csp.code ,
    to_number(pos_terminals.CODE),
    pos_terminals.CODE,
    pos_action.CUSTOMER_APPLIED_FEES,
    to_number(TO_CHAR(pos_action.OLD_VALUE, '0000000000.000')) Opened_Balance,
    to_number(TO_CHAR(pos_action.NEW_VALUE, '0000000000.000')) closing_balance,
    pos_action.AMOUNT,
    pos_action.ACTION_DATE,
    pos_action.ACTION_ID,
    actionlookup.action_nature,
    users.user_name,
    merchants.code,
    merchants.name_primary_lang,
    actionlookup.code,
    actionlookup.NAME_PRIMARY_LANG,
    pos_action.trx_client_date,
    pos_action.fw_cust_ref_num,
    usageLookup.code,
    pos_action.DESCRIPTION,
    account_currency.code,
    pos_action.amount_in_acct_currency,
    currency.code,
    pos_action.trx_fx_rate ,
    pos_terminals.cash_collector_id,
    pos_action.bill_type_code ,
    pos_action.BILLING_ACCT_NUM
  FROM POS_SOF.POS_TERMINALS_ACTIONS pos_action
  INNER JOIN POS_SOF.pos_terminals pos_terminals
  ON pos_action.terminal_id = pos_terminals.id and pos_terminals.PRIMARY_ACCT_ID IS NULL
  INNER JOIN POS_SOF.CURRENCY currency
  ON pos_action.trx_currency=currency.id
  INNER JOIN merchants_lookup MERCHANTS
  ON pos_terminals.merchant_id= MERCHANTS.id
  INNER JOIN POS_SOF.pos_usage_lookup usageLookup
  ON usageLookup.id = pos_terminals.usage_id
  INNER JOIN csp csp
  ON MERCHANTS.csp_id= csp.id
  INNER JOIN POS_SOF.ACTION_TYPES_LOOKUP actionlookup
  ON pos_action.action_type_id = actionlookup.id
  INNER JOIN POS_SOF.SOF_USER users
  ON pos_action.actor_id                   = users.id
  INNER JOIN POS_SOF.CURRENCY account_currency
  ON pos_terminals.currency_id=account_currency.id
  WHERE (UPPER(actionlookup.action_nature) = UPPER('cr')
  OR UPPER(actionlookup.action_nature)     = UPPER('dr'))/* Build # SEE_REP_078 */;

