CREATE OR REPLACE FORCE VIEW "POS_SETTLEMENTS" ("POS_CODE", "ACTION_DATE", "FEES", "AMOUNT", "TERM_COUNT", "CSP_ID") AS SELECT pos_terminals.code,
     TO_DATE(TO_CHAR(DEB.ACTION_DATE,'DD-MM-YYYY'),'DD-MM-YYYY'),
    SUM(
    CASE
      WHEN REV.FEES IS NULL
      THEN DEB.CUSTOMER_APPLIED_FEES
      ELSE 0
    END) fees ,
    SUM(
    CASE
      WHEN REV.AMOUNT IS NULL
      THEN DEB.AMOUNT
      ELSE 0
    END) amount,
    SUM(
    CASE
      WHEN pos_terminals.code IS NULL
      THEN 1
      ELSE 0
    END ) poscode,
     merchant.csp_id
  FROM POS_SOF.pos_terminals pos_terminals
  INNER JOIN POS_SOF.POS_TERMINALS_ACTIONS DEB
  ON DEB.terminal_id = pos_terminals.id
  INNER JOIN POS_SOF.ACTION_TYPES_LOOKUP action_lookup
  ON action_lookup.id            = DEB.action_type_id
  AND (upper(action_lookup.code) = upper('DEB'))
   inner join POS_SOF.merchants_lookup merchant
  on pos_terminals.merchant_id=merchant.id
  LEFT JOIN
    (SELECT pos_terminal.code POSCODE ,
      pos_actions.ACTION_DATE ACTIONDATE ,
      pos_actions.CUSTOMER_APPLIED_FEES FEES ,
      pos_actions.AMOUNT ,
      action_lookups.code ,
      pos_actions.ASYNIC_ID
    FROM POS_SOF.pos_terminals pos_terminal
    INNER JOIN POS_SOF.POS_TERMINALS_ACTIONS pos_actions
    ON pos_actions.terminal_id = pos_terminal.id
    INNER JOIN POS_SOF.ACTION_TYPES_LOOKUP action_lookups
    ON action_lookups.id            =pos_actions.action_type_id
    AND (upper(action_lookups.code) = upper('REV_DEB'))
    ) REV ON DEB.ASYNIC_ID          = REV.ASYNIC_ID
  GROUP BY pos_terminals.code, TO_DATE(TO_CHAR(DEB.ACTION_DATE,'DD-MM-YYYY'),'DD-MM-YYYY'), merchant.csp_id
  ORDER BY TO_DATE(TO_CHAR(DEB.ACTION_DATE,'DD-MM-YYYY'),'DD-MM-YYYY') , pos_terminals.code /* Build # SEE_REP_059 */;

