CREATE OR REPLACE FORCE VIEW "ACCOUNTS_STATUS_LOGS" ("ACCOUNT_ID", "ACCOUNT_CODE", "ACTION_DATE", "OLD_STATUS", "NEW_STATUS", "CSP_ID", "TERMINALS_COUNT") AS WITH MAXACTION AS
  ( SELECT DISTINCT ACTION.ACCOUNT_ID                             AS ACCOUNT_ID,
    MAX(ACTION.ACTION_DATE) OVER (PARTITION BY ACTION.ACCOUNT_ID) AS MAXDATE
  FROM POS_SOF.SOF_ACCOUNT_COMMON_ACTIONS ACTION
  WHERE (ACTION.ACTION_TYPE_ID IN
    (SELECT ACTIONTYPE.ID
    FROM POS_SOF.ACTION_TYPES_LOOKUP ACTIONTYPE
    WHERE ACTIONTYPE.CODE IN ('STS_UPDT')
    ) )
  )
SELECT TERMINAL.ID ,
  TERMINAL.CODE ,
  ACTION2.ACTION_DATE,
  ACTION2.OLD_VALUE,
  ACTION2.NEW_VALUE,
  MERCHANTS.CSP_ID ,
  (SELECT COUNT(TERMIANLS_DETAILS.ID)
    FROM POS_SOF.POS_TERMINAL_DETAILTS TERMIANLS_DETAILS
    WHERE TERMIANLS_DETAILS.TERMINAL_STATUS_ID <> 4
    AND TERMIANLS_DETAILS.POS_TERMINAL_ID       =TERMINAL.ID
    ) TER_COUNT
FROM POS_SOF.SOF_ACCOUNT_COMMON_ACTIONS ACTION2
INNER JOIN MAXACTION
ON ACTION2.ACTION_DATE = MAXACTION.MAXDATE
INNER JOIN POS_SOF.POS_TERMINALS TERMINAL
ON TERMINAL.ID = ACTION2.ACCOUNT_ID AND TERMINAL.PRIMARY_ACCT_ID IS NULL
INNER JOIN POS_SOF.MERCHANTS_LOOKUP MERCHANTS
ON TERMINAL.MERCHANT_ID   =MERCHANTS.ID
WHERE ACTION2.ACCOUNT_ID = MAXACTION.ACCOUNT_ID
  /* Build # SEE_REP_078 */;

