-- changeset Mostafa.Abdullatif:1658241639693-1125
CREATE OR REPLACE FORCE VIEW "ACCOUNT_NON_FINANCIAL_ACTIONS" ("ACCOUNT_ID", "ACCOUNT_CODE", "ACCOUNT_STATUS", "ACTOR_ID", "ACTION_TYPE_ID", "ACTION_DATE", "MERCHANT", "CSP_ID", "CSP_CODE", "OLD_VALUE", "NEW_VALUE", "USER_NAME") AS SELECT ACCOUNTS.ID,
    ACCOUNTS.CODE,
    ACCOUNTS.TERMINAL_STATUS_ID ,
    ACTIONS.ACTOR_ID ,
    ACTIONS.ACTION_TYPE_ID ,
    ACTIONS.ACTION_DATE ,
    MERCHANT.CODE
    ||' - '
    ||MERCHANT.NAME_PRIMARY_LANG,
    MERCHANT.CSP_ID ,
    CSP.CODE ,
    ACTIONS.OLD_VALUE ,
    ACTIONS.NEW_VALUE ,
    USERS.USER_NAME
  FROM POS_SOF.SOF_ACCOUNT_COMMON_ACTIONS ACTIONS
  INNER JOIN POS_SOF.ACTION_TYPES_LOOKUP ACTION_TYPES
  ON ACTIONS.ACTION_TYPE_ID              = ACTION_TYPES.ID
  AND Upper(ACTION_TYPES.ACTION_NATURE) <>Upper('Dr')
  AND Upper(ACTION_TYPES.ACTION_NATURE) <>Upper('Cr')
  INNER JOIN POS_SOF.POS_TERMINALS ACCOUNTS
  ON ACTIONS.ACCOUNT_ID = ACCOUNTS.ID AND ACCOUNTS.PRIMARY_ACCT_ID IS NULL
  INNER JOIN POS_SOF.MERCHANTS_LOOKUP MERCHANT
  ON ACCOUNTS.MERCHANT_ID                                              = MERCHANT.ID
   INNER JOIN POS_SOF.CSP CSP
  ON MERCHANT.CSP_ID                                              = CSP.ID
  INNER JOIN POS_SOF.SOF_USER USERS ON  ACTIONS.ACTOR_ID = USERS.ID
  WHERE ACCOUNTS.TERMINAL_STATUS_ID                                   <> 4
  AND TO_DATE(TO_CHAR(ACTIONS.ACTION_DATE,'DD-MM-YYYY'),'DD-MM-YYYY') >= TO_DATE(TO_CHAR((SYSDATE - 10),'DD-MM-YYYY'),'DD-MM-YYYY')
    /* Build # SEE_REP_078 */;

-- changeset Mostafa.Abdullatif:1658241639693-1126
CREATE OR REPLACE FORCE VIEW "CARD_TRX_DETAIL" ("POS_CODE", "MERCHANT_NAME", "MERCHANT_CODE", "BANK_TERMINAL_ID", "USAGE_ID", "USAGE_NAME", "BANK_CODE", "BANK_BIN", "AUTH_ID", "ISSUER_BANK_ID", "CARD_PAN", "RRN", "EFF_DT", "FCRN", "BILL_TYPE_CODE", "REVERSAL_FLAG", "TRX_DATE", "BALANCE_BEFORE", "TRX_AMOUNT", "FEES", "BALANCE_AFTER", "PMT_TYPE", "RECON_STATUS_CODE", "RECON_STATUS_NAME", "BANK_SETTLEMENT_DATE", "FAWRY_MERCHANT_BANK_ID", "RECEIPT_NUMBER", "ORDER_INFO", "IS_MICRO_PMT", "BILLING_ACCOUNT_NUM", "COMMAND_ID", "ACTION_STATUS_ID", "MERCHANT_TRX_REF") AS SELECT terminal.code         AS pos_code,
    merchant.name_primary_lang AS merchant_name,
    merchant.code              AS merchant_code,
    cardaccount.bank_terminal_id,
    usagelookup.id                AS usage_id,
    usagelookup.name_primary_lang AS usage_name,
    bank.code                     AS bank_code,
    bank.bin                      AS bank_bin,
    action.auth_id,
    action.issuer_bank_id,
    action.acct_id              AS card_pan,
    action.retrieval_ref_number AS RRN,
    action.EFF_DT,
    pmtdetails.fw_cust_ref_num AS FCRN,
    pmtdetails.bill_type_code ,
    pmtdetails.reversal_flag,
    action.action_date,
    to_number(TO_CHAR(action.old_value, '0000000000.000')) AS balance_before,
    (CASE WHEN pmtdetails.trx_amount IS NOT NULL THEN pmtdetails.trx_amount ELSE action.auth_amount END ) trx_amount,
    (CASE WHEN pmtdetails.customer_applied_fees IS NOT NULL THEN pmtdetails.customer_applied_fees ELSE 0 END )   AS fees,
    to_number(TO_CHAR(action.new_value, '0000000000.000')) AS balance_after,
    pmtdetails.pmt_type,
    reconstatus.code              AS recon_status_code,
    reconstatus.name_primary_lang AS recon_status_name,
    action.BANK_SETTLEMENT_DATE,
    action.FAWRY_MERCHANT_BANK_ID,
    action.RECEIPT_NUMBER,
    action.ORDER_INFO ,
    action.IS_MICRO_PMT,
   pmtdetails.BILLING_ACCT_NUM,
	action.ccard_action_command_id AS command_id,
	action.ccard_action_status AS action_status_id,
	action.merchant_trx_ref
