  INNER JOIN ccard_recon_status_lookup reconStatus
  ON reconstatus.id = action.reconciliation_status_id
  INNER JOIN pos_ccard_account cardAccount
  ON action.pos_ccard_account_id = cardaccount.id
  INNER JOIN AQUIRE_BANK bank
  ON cardaccount.aquire_bank_id = bank.id
  LEFT JOIN ccard_actions_pmt_details pmtDetails
  ON pmtdetails.ccard_actions_id = action.id
  INNER JOIN pos_terminals terminal
  ON terminal.id = cardaccount.pos_terminal_id
  INNER JOIN merchants_lookup merchant
  ON terminal.merchant_id = merchant.id
  INNER JOIN pos_usage_lookup usageLookup
  ON usagelookup.id                = terminal.usage_id
     /* Build # SEE_REP_075 */;

-- changeset Mostafa.Abdullatif:1658241639693-1127
CREATE OR REPLACE FORCE VIEW "CARD_TRX_STATUS" ("flag", "name") AS SELECT
    CASE
      WHEN action_status.code = 'A'
      THEN 'N'
      WHEN action_status.code = 'V'
      THEN 'Y'
      ELSE '0'
    END AS status_flag,
    CASE
      WHEN action_status.code = 'A'
      THEN 'Debited'
      WHEN action_status.code = 'V'
      THEN 'Debit-reversed'
      ELSE 'All'
    END AS status_name
  FROM ccard_actions_status_lk action_status/* Build # SEE_REP_059 */;

