    (terminal.credit_limit - terminal.balance) AS CASH_IN_HAND,
    terminal.balance                           AS COLLECTION_RANGE,
    terminal_status.name_primary_lang          AS STATUS,
    terminal.description_primary_lang          AS DESCRIPTION,
    currency.code                              AS CURRENCY,
