	to_number(pos_terminals.CODE),
  pos_terminals.CODE,
    pos_action.CUSTOMER_APPLIED_FEES,
    to_number(TO_CHAR(pos_action.OLD_VALUE, '0000000000.000')) Opened_Balance,
    to_number(TO_CHAR(pos_action.NEW_VALUE, '0000000000.000')) closing_balance,
    pos_action.AMOUNT,
