CREATE OR REPLACE FORCE VIEW "USER_LAST_ACCESS_DATE" ("USER_ID", "USER_NAME", "ACTION_DATE") AS SELECT "SOF_USER"."ID",
    "SOF_USER"."USER_NAME" ,
    MAX("USER_ACTIONS"."ACTION_DATE")
  FROM "POS_SOF"."SOF_USER"
  LEFT OUTER JOIN (SELECT MAX(FINANCIAL.ACTION_DATE) ACTION_DATE, FINANCIAL.ACTOR_ID
  FROM "POS_SOF"."POS_TERMINALS_ACTIONS" FINANCIAL GROUP BY FINANCIAL.ACTOR_ID
  UNION ALL
  SELECT MAX(NON_ACC_FINANCIAL.ACTION_DATE) ACTION_DATE , NON_ACC_FINANCIAL.ACTOR_ID  FROM
  "POS_SOF"."SOF_ACCOUNT_COMMON_ACTIONS" NON_ACC_FINANCIAL  GROUP BY NON_ACC_FINANCIAL.ACTOR_ID
  UNION ALL
  SELECT MAX(NON_TRM_FINANCIAL.ACTION_DATE) ACTION_DATE , NON_TRM_FINANCIAL.ACTOR_ID
  FROM "POS_SOF"."SOF_TERMINAL_COMMON_ACTIONS" NON_TRM_FINANCIAL  GROUP BY NON_TRM_FINANCIAL.ACTOR_ID
